/** 
 * 页面中上下跳转 
 * @method WpOptArea 
 * @param {String} dom 函数对象
 * @param {Object} option 参数对象
 * @return {Null} 无返回值
 */
function WpOptArea(t,o){this.element=$(t);this.options=o}WpOptArea.prototype={init:function(){this.initUi();this.initEvents()},initUi:function(){var t=this.options;this.oTop=this.element.find(t.oTop);this.oDowm=this.element.find(t.oDowm)},initEvents:function(){var t=this;$(window).scroll(function(){if($(window).scrollTop()<20){t.oTop.hide()}else{t.oTop.show()}if($(window).height()+$(window).scrollTop()>document.body.scrollHeight-20){t.oDowm.hide()}else{t.oDowm.show()}});t.element.on("click","a",function(){var t=$(this).index();if(t==0){$("body,html").animate({scrollTop:0},250)}else{$("body,html").animate({scrollTop:$("footer").offset().top+$("footer").outerHeight()},250)}});t.element.find(".wp-show-hide").on("click",function(){var o=$(this),i=o.data("hide");if(i==="false"||!i){i=o.data("hide","true");t.element.find("a").fadeOut("fase");o.find("i").attr("title","展开").removeClass("glyphicon-arrow-right").addClass("glyphicon-arrow-left")}else{i=o.data("hide","false");t.element.find("a").fadeIn("fase");o.find("i").attr("title","收起").removeClass("glyphicon-arrow-left").addClass("glyphicon-arrow-right")}})}};function WpAnimate(t){$(t.dom).on("click","a",function(){var o=$(this).attr("href");$("body,html").animate({scrollTop:$(o).offset().top-t.num},250)})}var App=function(){$(function(){$("img[data-original]").lazyload({effect:"fadeIn",placeholder:"http://wangpq.github.io/assets/app/images/loading.gif"});new WpAnimate({dom:".sort-bar .bd",num:70});new WpOptArea(".wp-opt-area",{oTop:".wp-btn-top",oDowm:".wp-btn-down"}).init()})};(function(){App()})();